package hcl;

public class Operation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int res1=-5+8*6;
		float res2=(55+9)%9;
		float res3=20+-3*5/8;
		float res4=5+15/3*2-8%3;
		System.out.println("-5+8*6 = "+res1);
		System.out.println("(55+9)%9 = "+res2);
		System.out.println("20+-3*5/8 = "+res3);
		System.out.print("5+15/3*2-8%3 = "+res4);
	}

}
